import React, { useState } from 'react';
import './App.css';
import Roadmap from './components/Roadmap';
import Header from './components/Header';
import FinancialSummary from './components/FinancialSummary';
import GoalModal from './components/GoalModal';

function App() {
  const [goals, setGoals] = useState([
    {
      id: 1,
      title: "Emergency Fund",
      description: "Save $2,000 for unexpected expenses",
      targetAmount: 2000,
      currentAmount: 800,
      category: "emergency",
      completed: false
    },
    {
      id: 2,
      title: "Tuition Payment",
      description: "Pay next semester's tuition of $8,000",
      targetAmount: 8000,
      currentAmount: 3200,
      category: "tuition",
      completed: false
    },
    {
      id: 3,
      title: "401k Contribution",
      description: "Contribute $6,000 to 401k this year",
      targetAmount: 6000,
      currentAmount: 1800,
      category: "retirement",
      completed: false
    },
    {
      id: 4,
      title: "Graduation Fund",
      description: "Save $5,000 for post-graduation expenses",
      targetAmount: 5000,
      currentAmount: 1200,
      category: "graduation",
      completed: false
    }
  ]);

  const [showModal, setShowModal] = useState(false);
  const [selectedGoal, setSelectedGoal] = useState(null);

  const updateGoalProgress = (goalId, newAmount) => {
    setGoals(prevGoals => 
      prevGoals.map(goal => 
        goal.id === goalId 
          ? { ...goal, currentAmount: newAmount, completed: newAmount >= goal.targetAmount }
          : goal
      )
    );
  };

  const addGoal = (newGoal) => {
    setGoals(prevGoals => [...prevGoals, { ...newGoal, id: Date.now() }]);
  };

  const openGoalModal = (goal = null) => {
    setSelectedGoal(goal);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedGoal(null);
  };

  const totalTarget = goals.reduce((sum, goal) => sum + goal.targetAmount, 0);
  const totalCurrent = goals.reduce((sum, goal) => sum + goal.currentAmount, 0);
  const overallProgress = (totalCurrent / totalTarget) * 100;

  return (
    <div className="App">
      <Header />
      <div className="main-content">
        <FinancialSummary 
          totalTarget={totalTarget}
          totalCurrent={totalCurrent}
          overallProgress={overallProgress}
          onAddGoal={() => openGoalModal()}
        />
        <Roadmap 
          goals={goals}
          onGoalClick={openGoalModal}
          onUpdateProgress={updateGoalProgress}
        />
      </div>
      {showModal && (
        <GoalModal
          goal={selectedGoal}
          onClose={closeModal}
          onSave={selectedGoal ? updateGoalProgress : addGoal}
        />
      )}
    </div>
  );
}

export default App;
